(77, 'Lydia', '<h1>Information:</h1>\n<ul>\n<li>Race: <a href=\"wiki.php?s=Demihuman\">Demihuman ([some animal idk])</a></li>\n<li>Status: Alive</li>\n<li>Intelligence: High</li>\n<li>Magic Circles: 10 & 1 Hate</li>\n<li>Magic type: <a href=\'wiki.php?s=Hate Magic\'>Hate</a></li>\n<li>Alignment: Neutral Evil</li>\n<li>Year born: 2486 WR</li>\n</ul>\n<h2>Extra info:</h2>\n<ul>\n<li>Current godess of Hate</li>\n</ul>\n<h1>Description</h1>\n\n<h1>History</h1>\n', '#character#new-god-gen3#unfinished', 1, ';Greneha;'),
(80, 'Bazella', '<h1>Information:</h1>\n<ul>\n<li>Race: <a href=\"wiki.php?s=Human\">Human</a></li>\n<li>Status: Dead</li>\n<li>Intelligence: Medium (cunning)</li>\n<li>Magic Circles: 10 & 1 chaos</li>\n<li>Magic type: <a href=\'wiki.php?s=Chaos Magic\'>Chaos Magic</a></li>\n<li>Alignment: Chaotic Evil</li>\n<li>Year born: 2970 WR</li>\n</ul>\n<h2>Extra info:</h2>\n<ul>\n<li><strong>Was</strong> the current generations goddess of chaos</li>\n</ul>\n<h1>Description</h1>\n\n<h1>History</h1>\n', '#character#new-god-gen3#unfinished', 1, ';Oscar;Phera;');
