INSERT INTO `magic` (`id`, `name`, `origin`, `complexity`, `fail_rate`, `cast_time`, `rules`, `effects`, `limits`, `tags`, `other`, `is_mod`) VALUES
(295, 'Ability boost', 'Love', 3, 25, 1, '-', '- Boosts ability of any target ally', '- lasts for 3 rounds', '- #static-spell \r\n- #Advanced-concept\r\n- #magic-system-1', '-', 0),
(296, 'Artus', 'Existence', 4, 50, 1, '- Can only grow limbs specific to your race', '- Gain another limb of your choosing', '- max two times the amount of limbs specific to your race', '- #dynamic-spell \r\n- #Hard-concept\r\n- #skill-posibility \r\n- #magic-system-1', '- Upon fail voids all new limbs', 0),
(297, 'Basic healing', 'Love', 1, 25, 1, '- Only works with direct contact', '- Repairs broken tissue\r\n- Stops up to moderate bleeding', '- Cannot create tissue (can only reconnect, ex. cuts or broken bones)', '- #static-spell\r\n- #Advanced-concept \r\n- #skill-posibility \r\n- #magic-system-1', '-', 0),
(298, 'Basic Telepathy', 'Truth', 2, 10, 0, '- requires direct line of sight', '- telepathic communication between two beings', '- 1 km range\r\n- both beings need to speak the same language', '- #static-spell \r\n- #Intermediate-concept \r\n- #magic-system-1', '- works for up to an hour\r\n- Doesn\'t require focus', 0),
(299, 'Basic understanding', 'Truth', 1, 25, 0, '- the more you know the being, the more you understand', '- Understand the intentions of another being', '-', '- #static-spell \r\n- #Advanced-concept\r\n- #magic-system-1', '-', 0),
(300, 'Brimstone skin', 'Hate', 4, 25, 0, '-', '- Anyone who touches the user receives burn-like pain and even injury with enough contact\r\n- Works through equipment and physical ranged attacks\r\n- Effect lasts for 3 rounds', '- Damage doesn\'t work through magic attacks', '- #static-spell \r\n- #Advanced-concept \r\n- #skill-posibility \r\n- #magic-system-1', '- Upon fail paralyzes user for a round', 0),
(301, 'Chains of Order', 'Order', 6, 50, 2, '- Needs solid matter near target', '- Chains a target to the ground or any solid surface (wall or roof)\r\n- Targets movements are severely limited', '- Chains can be dispelled with 8 circles', '- #static-spell \r\n- #Advanced-concept \r\n- #magic-system-1', '- effect lasts for 5 rounds', 0),
(302, 'Chains of sea', 'Order', 3, 25, 1, '- Needs solid matter near target\r\n- Needs direct line of sight', '- Chains a target to the ground or any solid surface (wall or roof)\r\n- Targets movements are somewhat limited', '- Chains can be broken with enough force\r\n- Chains can be dispelled with 5 circles', '- #dynamic-spell \r\n- #Advanced-concept\r\n- #magic-system-1', '- Effect lasts for 2 rounds', 0),
(303, 'Chaos effect', 'Chaos', 5, 25, 1, '-', '- Implements randomness (d20) into any action', '-', '- #static-spell \r\n- #Advanced-concept \r\n- #magic-system-1', '- Somewhat useless now that we roll for everything :P', 0),
(304, 'Chaos enhance', 'Chaos', 5, 50, 0, '- Can be cast after any roll', '- Performs another random roll (with the same die)\r\n- lower half decreases luck, upper half increases it', '- can only be activated once per roll', '- #static-spell \r\n- #Hard-concept \r\n- #skill-posibility \r\n- #magic-system-1', '- If the final value is 0 or less, the caster falls unconscious \r\n- example of luck change with a d20:\r\n\r\n| d20 roll | luck value |\r\n| -------- | ---------- |\r\n| 1        | -10        |\r\n| 2        | -9         |\r\n| 3        | -8         |\r\n| 4        | -7         |\r\n| 5        | -6         |\r\n| 6        | -5         |\r\n| 7        | -4         |\r\n| 8        | -3         |\r\n| 9        | -2         |\r\n| 10       | -1         |\r\n| 11       | +1         |\r\n| 12       | +2         |\r\n| 13       | +3         |\r\n| 14       | +4         |\r\n| 15       | +5         |\r\n| 16       | +6         |\r\n| 17       | +7         |\r\n| 18       | +8         |\r\n| 19       | +9         |\r\n| 20       | +10        |', 0),
(305, 'Charm', 'Love', 4, 75, 1, '- both targets must be within 5 meters', '- Enhances ones affection towards another\r\n- Victim doesn\'t want to:\r\n	- injure the other\r\n	- defy the other\r\n- victim will try to help the other\r\n- lasts for a day', '- Doesn\'t work on individuals with strong willpower or either high or extremely low inteligence\r\n- Victim wont perform extreme tasks', '- #static-spell \r\n- #Master-concept \r\n- #magic-system-1', '- Upon fail disables this magic for a day\r\n- both targets are chosen by the caster', 0),
(306, 'Communication network', 'Truth', 5, 25, 2, '- All targets need a direct line of sight when casting', '- Free communication throughout the entire party', '- Stops working immediately when the caster falls unconscious\r\n- works for up to a day', '- #static-spell \r\n- #Advanced-concept \r\n- #skill-posibility \r\n- #magic-system-1', '- Caster can keep the connection indefinitely by casting again even without direct line of sight\r\n- doesn\'t require focus', 0),
(307, 'Copy spell', 'Lie', -152, 10, 0, '- Needs at least one less complexity than the targeted spell', '- Creates a weaker version of the targeted spell', '- works up to 7th tier complexity spells', '- #static-spell \r\n- #Intermediate-concept \r\n- #magic-system-1', '- Upon fail causes the caster to suffer the fail effects of targeted spell', 0),
(308, 'Create Elemental', 'Creation', 7, 10, 1, '-', '- Creates an elemental from basic elements \r\n- Elemental will follow commands from the caster', '- Doesn\'t have a consciousness \r\n- Up to 10 elementals may be formed by the caster', '- #dynamic-spell \r\n- #Master-concept \r\n- #magic-system-1', '- Elemental can cast up to ten 4 tier spells\r\n- Elemental will perish upon casting all of their spells', 0),
(309, 'Create Matter', 'Creation', 5, 5, 1, '-', '- creates matter the size of a cubic decimeter', '- Only basic matter can be created (dirt, stone, water...), however the limit is lessened with every upscale', '- #dynamic-spell \r\n- #Basic-concept \r\n- #magic-system-1', '-', 0),
(310, 'Create Seedling', 'Creation', 8, 95, 20, '- Needs a target vessel that can hold a soul', '- Forms a magic circle seed', '-', '- #static-spell\r\n- #Impossible-concept\r\n- #magic-system-1', '- The seed can take years to grow into a fully functional circle\r\n- Alternatively, the seed can receive a huge burst of energy (comparable to 10 circles), in which case, the circle is slightly corrupted', 0),
(311, 'Create Vessel', 'Creation', 9, 95, 50, '- depending on the size and complexity of the body, the cast time can change', '- Creates an organic body with the ability to hold a soul', '-', '- #dynamic-spell\r\n- #Impossible-concept\r\n- #magic-system-1', '- The body will function for a while, but shuts down after a few hours, unless it receives a soul', 0),
(312, 'Curse projectile', 'Hate', 2, 10, 1, '-', '- Inflicts pain to the target and light burns\r\n- Ignores armor', '- Doesn\'t affect beings with 5 or more circles', '- #dynamic-spell \r\n- #Intermediate-concept \r\n- #magic-system-1', '-', 0),
(313, 'Dead world illusion', 'Lie', 5, 50, 2, '- Requires eye contact with the target (at least once during cast time)', '- Creates an illusion of a fake world, where all living beings die on the spot\r\n- Effect lasts indefinitely', '- If target realizes they\'re in a fake world the effect only lasts up to 1 round', '- #static-spell \r\n- #Hard-concept \r\n- #skill-posibility \r\n- #magic-system-1', '- Upon fail disables this magic targets the caster', 0),
(314, 'Endure', 'Existence', 3, 25, 0, '- requires focus for the duration of the effect (but caster doesn\'t suffer consequences of losing focus)', '- Makes caster somewhat invulnerable (most physical attacks don\'t work and some magic too)\r\n- Paralyzes caster\r\n- Effect lasts for 3 rounds, but can be undone by losing focus', '- Caster needs to be on stable ground to keep focus for this spell', '- #static-spell \r\n- #Advanced-concept \r\n- #skill-posibility \r\n- #magic-system-1', '- Upon fail makes caster dizzy', 0),
(315, 'Fact', 'Truth', 5, 50, 1, '-', '- Figure out a desired fact', '- Can only be cast once a day\r\n- Not omniscient', '- #static-spell \r\n- #Hard-concept \r\n- #magic-system-1', '- Upon fail disables this magic for a day', 0),
(316, 'Familiar command', 'Love', 5, 10, 0, '- Needs an allied familiar as a target', '- Make a familiar perform one active magic for you\r\n- All of the magics effects apply to the familiar instead of you', '- Familiar needs to have enough magic circles (or suffers the consequences)\r\n- Caster has less control over the magic', '- #static-spell \r\n- #Intermediate-concept \r\n- #skill-posibility \r\n- #magic-system-1', '- Upon fail knocks the familiar unconscious', 0),
(317, 'Fireball', 'Creation', 3, 10, 1, '- starting point of the projectile cannot be inside solid or liquid matter', '- Creates a ball of fire that can be thrown', '- throwing distance depends on the strength of the caster', '- #dynamic-spell \r\n- #Intermediate-concept \r\n- #magic-system-1', '- Upon fail explodes in hand', 0),
(318, 'Form Bullet', 'Creation', 4, 5, 1, '- Needs material from the environment', '- Form a bullet from rock, ice, or any matter around\r\n- The bullet will be the size of around a cubic centimeter', '-', '- #dynamic-spell \r\n- #Basic-concept \r\n- #magic-system-1', '- cast time can be longer, which will cause the bullet to be bigger, faster and denser\r\n- upon fail, erupts in a small explosion', 0),
(319, 'Form Golem', 'Creation', 6, 20, 3, '- Needs material from the environment', '- Creates a golem out of solid matter\r\n- Golem will obey basic commands from the caster', '- Can be made only with basic solid matter\r\n- Crumbles after 10 minutes \r\n- Golem has a very simple mind\r\n- Up to 10 golems may be formed by the caster', '- #dynamic-spell \r\n- #Hard-concept \r\n- #magic-system-1', '-', 0),
(320, 'Hide object', 'Lie', 3, 10, 1, '- Requires direct contact with target', '- Makes object/being almost invisible', '- can only be cast once per round', '- #static-spell \r\n- #Hard-concept \r\n- #magic-system-1', '- Can be cast on the caster', 0),
(321, 'Inspect', 'Truth', 3, 50, 3, '-', '- Find out more information about something in your field of view', '- what you learn is slightly limited by your current knowledge', '- #static-spell \r\n- #Hard-concept\r\n- #magic-system-1', '-', 0),
